DROP TABLE IF EXISTS tarjetas;
CREATE TABLE tarjetas (
    id_tarjeta integer PRIMARY KEY,
    id_marca_tarjeta integer NOT NULL,
    customer_id integer NOT NULL,
    numero integer NOT NULL UNIQUE CHECK(length(numero)<=20),
    CVV integer NOT NULL, 
    fecha_otorgamiento text NOT NULL, 
    fecha_expiracion text NOT NULL, 
    tipo_tarjeta text NOT NULL,
    CONSTRAINT fk_tarjeta
        FOREIGN KEY (id_marca_tarjeta)
        REFERENCES marcas_tarjeta(id_marca_tarjeta), 
        FOREIGN KEY (customer_id)
        REFERENCES cliente(customer_id),
        FOREIGN KEY (tipo_tarjeta)
        REFERENCES tipo_tarjeta(id_tipo_tarjeta)
    );


UPDATE tarjetas
SET id_marca_tarjeta = 3
WHERE length(numero) = 15;

UPDATE tarjetas
SET fecha_otorgamiento = SUBSTR(fecha_otorgamiento,7,4) || '-' ||
                         SUBSTR(fecha_otorgamiento,4,2) || '-' || 
                         SUBSTR(fecha_otorgamiento,1,2);

UPDATE tarjetas
SET fecha_expiracion =  SUBSTR(fecha_expiracion,7,4) || '-' ||
                        SUBSTR(fecha_expiracion,4,2) || '-' || 
                        SUBSTR(fecha_expiracion,1,2);